	if (!sCurrentInputFile) return 1;

	// switch to next file
	yy_switch_to_buffer(sCurrentInputFile->fBufferState);
	sOffset = sCurrentInputFile->fSavedOffset;
	sSourceIndex = sCurrentInputFile->fSourceIndex;

	// tell yylex() to continue
